// Configure an ML pipeline, which consists of two stages on one column: a StringIndexer and a OneHotEncoder.
import org.apache.spark.ml.feature.{OneHotEncoder, StringIndexer, VectorAssembler}
import org.apache.spark.ml.{Pipeline, PipelineModel}

// Fonction retournant une séquence de StringIndexer & OneHotEncoder pour un nom de catégorie donnée
def makeStageFunction(w: String) = {

    // Construction de notre StringIndexer
    // Le nom de colonne en entrée (à mapper) est indiqué via setInputCol
    // Le nom de colonne en sortie est celui en entrée suffixé par "Index"
    val stringIndexer = new StringIndexer()
      .setInputCol(w)
      .setOutputCol(w+"Index")

    // Construction de notre OneHotEncoder
    // Le nom de colonne en entrée est celui en sortie du StringIndexer précédemment créé
    // Le nom de colonne en sortie est celui en sortie du StringIndexer suffixé par ClassVec
    val oneHotEncoder = new OneHotEncoder()
      .setInputCol(stringIndexer.getOutputCol)
      .setOutputCol(stringIndexer.getInputCol + "ClassVec")
    Seq(stringIndexer, oneHotEncoder)
}

// Séquence des variables catégorielles non-numériques pour lesquelles nouss souhaitons transformer les différents états en vecteurs binaires.
val nonNumericalColumns = Seq("workclass", "education", "marital_status", "occupation", "relationship", "race", "sex", "native_country")
// Création des étapes à destination de notre pipeline ML.
// La méthode flatMap nous permet ici d'appliquer une transformation sur chaque valeur de nonNumericalColumns
// Nous transformons ici des noms de colonnes en séquence Seq(stringIndexer, oneHotEncoder)
val stages = nonNumericalColumns.flatMap(makeStageFunction)

// Etape pour le label (sans la transformation one-hot)
val label_stringIdx = new StringIndexer().setInputCol("income").setOutputCol("label")
// Combine une liste de colonnes en un vecteur de colonnes
val assembler = new VectorAssembler().setInputCols(nonNumericalColumns.map(w => w + "ClassVec").toArray).setOutputCol("features")
val finalStages = stages ++ Seq(label_stringIdx) ++ Seq(assembler)

// Création du pipeline et ajout des étapes
val pipeline = new Pipeline().setStages(finalStages.toArray)

