// Configure an ML pipeline, which consists of two stages on one column: a StringIndexer and a OneHotEncoder.
import org.apache.spark.ml.feature.{OneHotEncoder, StringIndexer, VectorAssembler}
import org.apache.spark.ml.{Pipeline, PipelineModel}

// Fonction retournant une séquence de StringIndexer & OneHotEncoder pour un nom de catégorie donné
def makeStageFunction(w: String) = {

    // Construction de notre StringIndexer
    // Le nom de colonne en entrée (à mapper) est indiqué via setInputCol
    // Le nom de colonne en sortie est celui en entrée suffixé par "Index"
    val stringIndexer = new StringIndexer()
      .setInputCol(w)
      .setOutputCol(w+"Index")

    // Construction de notre OneHotEncoder
    // Le nom de colonne en entrée est celui en sortie du StringIndexer précédemment créé
    // Le nom de colonne en sortie est celui en sortie du StringIndexer suffixé par ClassVec
    val oneHotEncoder = new OneHotEncoder()
      .setInputCol(stringIndexer.getOutputCol)
      .setOutputCol(stringIndexer.getInputCol + "ClassVec")
    Seq(stringIndexer, oneHotEncoder)
}

// Séquence des variables catégorielles non-numériques pour lesquelles nouss souhaitons transformer les différents états en vecteurs binaires.
val nonNumericalColumns = Seq("workclass", "education", "marital_status", "occupation", "relationship", "race", "sex", "native_country")
// Création des étapes à destination de notre pipeline ML.
// La méthode flatMap nous permet ici d'appliquer une transformation sur chaque valeur de nonNumericalColumns
// Nous transformons ici des noms de colonnes en séquence Seq(stringIndexer, oneHotEncoder)
val stages = nonNumericalColumns.flatMap(makeStageFunction)

// Etape pour le label (sans la transformation one-hot)
val label_stringIdx = new StringIndexer().setInputCol("income").setOutputCol("label")
// Combine une liste de colonnes (suffixées par ClassVec) en un vecteur de colonnes
val assemblerStage = new VectorAssembler().setInputCols(nonNumericalColumns.map(w => w + "ClassVec").toArray).setOutputCol("features")
val finalStages = stages ++ Seq(label_stringIdx) ++ Seq(assemblerStage)

// Création du pipeline et ajout des étapes
val pipeline = new Pipeline().setStages(finalStages.toArray)

// Adapte le pipeline au jeu de données d'entrée dataset.
// Nous obtenons un model que nous appliquons sur les données pour les tranformer.
val pipelineModel = pipeline.fit(dataset)

// Nous stockons des données transformées pour pouvoir les réutiliser par la suite sans avoir à tout réexécuter...
val datasetTransformed = pipelineModel.transform(dataset)

// Pour futures requêtes SQL
datasetTransformed.registerTempTable("datasetTransformed")

datasetTransformed.show()

import org.apache.spark.ml.classification.LogisticRegression
import org.apache.spark.ml.evaluation.BinaryClassificationEvaluator

// Implémentation du model de machine learning LogisticRegression
val lr = new LogisticRegression().setMaxIter(10).setRegParam(0.01)

// Création d'un estimateur en adaptant le modèle de régression logistique sur les données training issues du dataset
val lrEstimator = lr.fit(training)

// Exécution de l'estimateur sur les données test issues du dataset
val datasetEstimated = lrEstimator.transform(test);

// Evaluation des prédictions grâce à BinaryClassificationEvaluator
val bce = new BinaryClassificationEvaluator()

// Nous obtenons une estimation juste à 87.85%
bce.evaluate(datasetEstimated)

// Affichage des erreurs d'estimation
datasetEstimated.filter($"label" !== $"prediction").select("workclass", "education", "sex", "label","prediction").show()

